"use strict";function getLogin(){$("#mainForm form").on("submit",function(t){t.preventDefault(),$.ajax({url:"http://api.yuguoxy.com/api/member/login",type:"post",data:{username:$('input[name="userN"]').val(),password:$('input[name="passW"]').val()},success:function(t){1==t.resultCode?(alert("登录成功"),localStorage.setItem("user",JSON.stringify(t.resultInfo)),location.href="./index.html"):alert("登录失败请重新输入信息")}})})}getLogin();